package com.org.gen.day2;

public class LeapYear {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int year = 2016;
		if(year%4 == 0) {
			System.out.println("This year is Leap Year");
		}
		else {
			System.out.println("This year is not a Leap Year");
		}
		

	}

}
